mapper.select:
select * from wave where  1=1

    and matchId =#{matchId}



    and createTime >#{beginTime}


    and  #{endTime} >createTime


limit 10000

mapper.update:
update wave set
buy_s_p = #{buy_s_p}, buy_s_f = #{buy_s_f}, buy_p_f = #{buy_p_f}, buy_s_p_amount = #{buy_s_p_amount}, buy_s_f_amount = #{buy_s_f_amount}, buy_p_f_amount = #{buy_p_f_amount},
sale_s_p = #{sale_s_p}, sale_s_f = #{sale_s_f}, sale_p_f = #{sale_p_f}, sale_s_p_amount = #{sale_s_p_amount}, sale_s_f_amount = #{sale_s_f_amount}, sale_p_f_amount = #{sale_p_f_amount}
where id = #{id}
